const StoreContext = createContext();
const { Provider } = StoreContext;

const reducer = (state, action) => {
  switch (action.type) {
    case UPDATE_POSTS:
      return;
    case ADD_POST:
      return;
    case REMOVE_POST:
      return;
    case SET_CURRENT_POST:
      return;
    case SAVING:
      return;
    default:
      return state;
  }
};

