import React, { createContext, useReducer, useContext } from "react";

const TodosContext = createContext();
const { Provider } = TodosContext;

const reducer = (state, action) => {
  switch (action.type) {
    case "add":
      return {
        todos: [
          ...state.todos,
          { name: action.payload, id: state.todos.length }
        ]
      };
    case "remove":
      return {
        todos: state.todos.filter(id => id !== action.index)
      };

    default:
      throw new Error(`Invalid action type: ${action.type}`);
  }
};

const TodosProvider = ({ value = [], ...props }) => {
  const [state, dispatch] = useReducer(reducer, { todos: value });

  return <Provider value={[state, dispatch]} {...props} />;
};

const useTodosContext = () => {
  return useContext(TodosContext);
};

export { TodosProvider, useTodosContext };
