import React from "react";

export const ThemeContext = React.createContext("light");

export const themes = {
  light: {
    foreground: "#000000",
    background: "#eeeeee"
  },
  dark: {
    foreground: "#ffffff",
    background: "#222222"
  },
  green: {
    background: "#5cb85c",
    foreground: "#fefefe"
  }
};
